var AStar = (function () {
    function AStar(matrix, start, end) {
        this.startX = start.x;
        this.startY = start.y;
        this.endX = end.x;
        this.endY = end.y;
        this.matrix = matrix;
        this.grid = new PF.Grid(this.matrix);
        this.finder = new PF.AStarFinder();
    }
    AStar.prototype.goTo = function () {
    };
    AStar.prototype.findPath = function () {
        var path = this.finder.findPath(this.startX, this.startY, this.endX, this.endY, this.grid);
        return path;
    };
    return AStar;
}());
var Block = (function () {
    function Block(game, size, x, y) {
        this.size = size;
        this.x = x;
        this.y = y;
        this.game = game;
    }
    Block.prototype.draw = function () {
        this.game.context.beginPath();
        this.game.context.rect(this.x, this.y, this.size, this.size);
        this.game.context.fillStyle = 'red';
        this.game.context.fill();
        this.game.context.closePath();
    };
    return Block;
}());
var Board = (function () {
    function Board(game, matrix, blockSize, showGrid, drawBlocks) {
        this.game = game;
        this.matrix = matrix;
        this.blockSize = blockSize;
        var rows = matrix.length;
        var column = matrix[0].length;
        this.width = column * this.blockSize;
        this.height = rows * this.blockSize;
        this.padding = 0;
        this.showGrid = showGrid;
        this.drawBlocks = drawBlocks;
    }
    Board.prototype.calcGridLoc = function () {
    };
    Board.prototype.update = function (matrix) {
        this.matrix = matrix;
    };
    Board.prototype.draw = function () {
        this.drawObstacle();
        if (this.showGrid) {
            this.drawBoard();
        }
    };
    Board.prototype.drawBoard = function () {
        this.game.context.save();
        this.game.context.beginPath();
        for (var x = 0; x <= this.width; x += this.blockSize) {
            this.game.context.moveTo(0.5 + x + this.padding, this.padding);
            this.game.context.lineTo(0.5 + x + this.padding, this.height + this.padding);
        }
        for (var y = 0; y <= this.height; y += this.blockSize) {
            this.game.context.moveTo(this.padding, 0.5 + y + this.padding);
            this.game.context.lineTo(this.width + this.padding, 0.5 + y + this.padding);
        }
        this.game.context.strokeStyle = "rgba(0, 0, 0, 0.3)";
        this.game.context.stroke();
        this.game.context.closePath();
        this.game.context.restore();
    };
    Board.prototype.drawObstacle = function () {
        for (var i = 0; i < this.matrix.length; i++) {
            for (var j = 0; j < this.matrix[i].length; j++) {
                if (this.matrix[i][j] == 1) {
                    var block = new Block(this.game, this.blockSize, j * this.blockSize + this.padding, i * this.blockSize + this.padding);
                    if (this.drawBlocks) {
                        block.draw();
                    }
                }
            }
        }
    };
    return Board;
}());
var Car = (function () {
    function Car(game, startPos, endPos, blockSize) {
        this.step = 1;
        this.done = false;
        this.radius = 10;
        this.game = game;
        this.blockSize = blockSize;
        this.x = blockSize / 2 + blockSize * startPos.x;
        this.y = blockSize / 2 + blockSize * startPos.y;
        this.previousX = this.x;
        this.previousY = this.y;
        this.endPos = endPos;
        this.speed = 1;
        this.calcPath();
    }
    Car.prototype.getX = function () {
        return this.x;
    };
    Car.prototype.getY = function () {
        return this.y;
    };
    Car.prototype.getPreviousX = function () {
        return this.previousX;
    };
    Car.prototype.getPreviousY = function () {
        return this.previousY;
    };
    Car.prototype.getEndPos = function () {
        return this.endPos;
    };
    Car.prototype.setPath = function (path) {
        this.path = path;
    };
    Car.prototype.draw = function () {
        this.game.context.beginPath();
        this.game.context.arc(this.x, this.y, this.radius, 0, 2 * Math.PI, false);
        this.game.context.fillStyle = 'green';
        this.game.context.fill();
        this.game.context.closePath();
    };
    Car.prototype.moveToPoint = function () {
        var xDone = false;
        var yDone = false;
        if (this.y == this.nextY) {
            yDone = true;
        }
        else {
            if (this.y - this.nextY > 0) {
                this.y -= this.speed;
            }
            else {
                this.y += this.speed;
            }
        }
        if (this.x == this.nextX) {
            xDone = true;
        }
        else {
            if (this.x - this.nextX > 0) {
                this.x -= this.speed;
            }
            else {
                this.x += this.speed;
            }
        }
        if (yDone && xDone) {
            return true;
        }
        return false;
    };
    Car.prototype.reset = function () {
        this.step = 1;
    };
    Car.prototype.calcPath = function () {
        this.aStar = new AStar(this.game.matrix, Util.getXYPostion(this.x, this.y, this.blockSize), this.getEndPos());
        this.path = this.aStar.findPath();
        if (this.path.length <= 1) {
            if (this.path.length == 0) {
                console.log("CRASH");
            }
            this.done = true;
        }
        else {
            var pos = new Pos(this.path[1][0], this.path[1][1]);
            this.nextX = pos.x * this.blockSize + this.blockSize / 2;
            this.nextY = pos.y * this.blockSize + this.blockSize / 2;
            this.game.drawObstacle(Util.getXYPostion(this.previousX, this.previousY, this.blockSize));
            this.game.drawObstacle(Util.getXYPostion(this.nextX, this.nextY, this.blockSize));
        }
    };
    Car.prototype.move = function () {
        this.previousX = this.x;
        this.previousY = this.y;
        if (this.moveToPoint()) {
            if (!this.done) {
                this.calcPath();
            }
        }
        this.draw();
    };
    return Car;
}());
